import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';

const useStyles = makeStyles({
  table: {
    minWidth: 650,
  },
});

const divStyle = {
    marginRight: "42%",
    marginBottom: "15px"
};

function createData(sensor, swtch, state, status) {
  return { sensor, swtch, state, status };
}

const rows = [
  createData(' ', 159, 6.0, 24, 4.0),
  createData(' ', 237, 9.0, 37, 4.3),
  createData(' ', 262, 16.0, 24, 6.0),
  createData(' ', 305, 3.7, 67, 4.3),
  createData(' ', 356, 16.0, 49, 3.9),
];

export default function SimpleTable() {
  const classes = useStyles();

  return (
    <TableContainer component={Paper}>
      <Table className={classes.table} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell align="right">Name</TableCell>
            <TableCell align="right">Switch</TableCell>
            <TableCell align="right">State</TableCell>
            <TableCell align="right">Status</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map(row => (
            <TableRow key={row.name}>
              <TableCell component="th" scope="row">
                {row.sensor}
              </TableCell>
              <TableCell align="right">{row.swtch}</TableCell>
              <TableCell align="right">{row.state}</TableCell>
              <TableCell align="right">{row.status}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
}
